import React  from "react";
import { useEffect } from "react";
import axios from "axios";
import { useState } from 'react';
import { Card } from "../components/Card";
import { CollapsableSection } from "../components/CollapsableSection";

export const Reference = () => {
  //const [content, setContent] = useState([{}]);
  const [groups, setGroups] = useState([]);

  const contentName = window.location.pathname;
  console.log(`${process.env.REACT_APP_URL_SERVER}${contentName}`)

  useEffect(() => {
    axios.get(`${process.env.REACT_APP_URL_SERVER}${contentName}`)
  .then(response => {
    //setContent(response.data.content)
    //const allGroups = Map.groupBy(response.data.content, ({ group }) => group);
    const content = response.data.content
    const allGroups = Map.groupBy(content, (content => {return content.group}));
    //console.log(allGroups)
    allGroups.forEach(element => {
      console.log(element)
    });
    setGroups(allGroups)
  })
  }, [])

  return (
    <div>
      { groups.length > 0 ? 
      groups
        .map(content =>
          <CollapsableSection
            title={content.key}
            content={content.value.map( word => 
            <Card
            symbol={word.symbol}
            meaning={word.meaning}
          />
          )}
          />
        )

        :

        <p>NO CONTENT</p>
    }
    </div>
  );
};
